package org.example;

import java.util.ArrayList;

import javafx.application.Application;
import javafx.geometry.Rectangle2D;
import javafx.scene.image.Image;
import javafx.scene.input.Clipboard;
import javafx.stage.Screen;
import javafx.stage.Stage;


public class App extends Application {
    static Window primaryWindow;

    static Clipboard clipBoard;
    static Rectangle2D bounds;

    static Stage primaryStage;

    static ArrayList<Window> windows;

    @Override
    public void start(Stage stage) throws Exception {
        primaryStage = stage;
        setup(primaryStage);

        //primaryWindow.start();
    }
    public static void main(String[] args) {
        launch(args);
    }

    private static void setup(Stage stage) {
        clipBoard = Clipboard.getSystemClipboard();
        Screen screen = Screen.getPrimary();

        bounds = screen.getVisualBounds();
        /*
        System.out.println(System.getProperty("user.dir"));
        File file = new File("/src/main/resources/icon.png");
        System.out.println(file.toString());
        */

        Image icon = new Image(App.class.getResourceAsStream("/icon.png"));
        stage.getIcons().add(icon);

        primaryWindow = new Window(stage);
    }

    public static void newWindow() {
        if(windows == null) {
            windows = new ArrayList<Window>();
            windows.add(primaryWindow);
        }

        Window newWindow = new Window(new Stage());
        windows.add(newWindow);
        newWindow.start();
    }



}
